const totalHours=24;$(document).ready(function(){$("#currency-select").on("change",function(){this.value;$("#electricity-unit").text(this.value+"/kW"),$("#profit-unit").text(this.value+"/BTC")}),$("#button-calculate").click(function(){$("#calculation-status").css("display","block"),$("#precalculate-status").css("display","none"),fetch("https://blockchain.info/q/hashrate").then(function(t){return t.json()}).then(function(t){var a=$("#efficiency").val(),c=$("#electricity").val();$("#hash-rate").val(t*Math.pow(10,-3));c=t*Math.pow(10,-6)*a*totalHours*c/900,c=Math.floor(100*c)/100;$("#answer").val(c),$("#calculation-status").css("display","none"),$("#precalculate-status").css("display","block")}).catch(function(t){$("#calculation-status").css("display","none"),$("#precalculate-status").css("display","block")})})});